.slider.rs-slider {
	background: none;
	border: 0 none;
	margin-left: auto;
	margin-right: auto;
	margin-top: 32px;
	max-height: 300px;
	max-width: 1135px;
	overflow: hidden;
	padding-left: 15px;
	padding-right: 15px;
	padding: 0;
	position: relative;
 }
 
.slider.load .tp-banner-container {visibility: hidden}
 
.slider.rs-slider .container {position: relative}
 
.slider.rs-slider .title {
	color: #fff;
	font-size: 40px;
	font-weight: 400;
	margin: 0
 }
 
.slider.rs-slider .description {
	color: #fff;
	font-size: 17px;
	line-height: 25px;
	margin: 0;
	padding: 0 15px
 }
 
.slider.rs-slider .btn {color: #fff}
 
	.slider.rs-slider .btn.orang {background: #ea4e16}
 
	.slider.rs-slider .btn.cherry {background: #752033}
 
.slider.rs-slider .phone-text {color: #fff;font-size:17px}
 
.rs-slider ul {
	list-style: none;
	margin: 0;
	padding: 0
 }
 
.tp-bannertimer {
	background: #777!important;
	background: rgba(0,0,0,0.1)!important;
	height: 5px!important
 }
 
.tp-bullets.simplebullets.navbar {height: 35px;padding:0}
 
.slider.rs-slider .tp-bullets.simplebullets .bullet {
	-moz-box-shadow: none;
	-webkit-border-radius: 7px;
	-webkit-box-shadow: none;
	-webkit-transition: all .3s linear;
	background: #fff;
	background: rgba(255,255,255,.5);
	border-radius: 7px;
	border: 0 none!important;
	box-shadow: none;
	display: inline-block;
	float: none;
	height: 14px;
	margin: 0 3px;
	transition: all .3s linear;
	vertical-align: top;
	width: 14px
 }
 
.tp-bullets.simplebullets .bullet.last {margin-right: 0}
 
.slider.rs-slider .tp-bullets.simplebullets .bullet:hover {
	-webkit-transition: all .3s linear;
	background: #fff;
	transition: all .3s linear
 }
 
.slider.rs-slider .tp-bullets.simplebullets .bullet.selected {
	-webkit-transition: all .3s linear;
	background: rgba(255,255,255,.7);
	transition: all .3s linear
 }
 
.slider.rs-slider .tparrows {
	-webkit-border-radius: 5px;
	-webkit-transition: background .3s linear;
	background: #0098ca;
	background: rgba(30,30,30,0.5);
	border-radius: 5px;
	cursor: pointer;
	height: 40px!important;
	transition: background .3s linear;
	width: 40px!important;
	z-index: 50!important
 }
 
	.slider.rs-slider .tparrows:before {
		color: #fff;
		display: inline-block;
		font-family: 'revicons';
		font-size: 19px
		font-style: normal;
		font-weight: 400;
		margin-right: 0;
		margin-top: 6px;
		speak: none;
		text-align: center;
		text-decoration: inherit;
		width: 40px;
	 }
 
.tp-leftarrow:before {content: 'e824'}
 
.tp-rightarrow:before {content: 'e825'}
 
.slider.rs-slider .tparrows:hover {
	-webkit-transition: background .3s linear;
	background: #0098ca;
	transition: background .3s linear
 }
 
.slider4container .tparrows:before,.slider4container .tparrows:hover,.slider4container .tparrows {color: #000!important}
 
.slider4container .tparrows {background: #fff!important;background:rgba(255,255,255,0.5)!important}
 
	.slider4container .tparrows:hover {background: #fff!important}
 
.slider.rs-slider .tp-loader {
	background-color: inherit;
	background-image: url(../img/svg/loader.svg),none;
