files.overall.less:
* {
    border-color: #e1e1e1;
    border-style: solid;
    border-width: 0;
    zoom: 1;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
}
h5{
	margin: 0 0 5px;
}
body{
	font-family: 'Arimo',sans-serif;
  height: 100%;
  line-height: 1.6;
  -webkit-appearance: none;
  -webkit-font-smoothing: antialiased;
  overflow-x: hidden;
	padding-top: 80px !important;
	@media screen and (max-width:1001px) {
	  padding: 0px !important;
	}	
}

img{
	max-width: 100%;
    height: auto;
}
.header .cart-header .dropdown-toggle, #footer .newsletter input:focus + .submit, .icon, .big-icon, .big-icon:visited, .service .icon, .close:hover, .close:focus, .img-thumbnail:hover .bg-images i:before, .box-404 h1, .gallery-images:hover .bg-images i:before, .features-block .header-box .icon-box, .features-block .header-box, .sidebar .newsletter input:focus + .submit, .sidebar .section .selected .close:hover, .package .title a, .package .price-box .price, .package .price-box .icon, .pricing .title a, .pricing .options li span, .pricing .options li.active, .banner-set .next:hover, .banner-set .prev:hover, .btn.btn-border, .btn.btn-border .caret, .product-remove:hover {
    color: #0098ca;
}

.phone-header a svg path, .search-header a svg path, .product .actions a svg path, .sidebar .wishlist .add-cart:hover path, .header .cart-header .dropdown-toggle .icon svg path, .search-active .search-submit svg path, .new-checkbox svg polygon, .product-bottom .related-products li .button-box .wishlist:hover svg path, .jslider .jslider-pointer svg path, .rating-box .rating svg polygon, .search-result .search-form .search-submit svg path {
    fill: #0098ca;
}

files.top-box.less:
#top-box {
	background: #0098ca;
    left: 0;
    margin-top: 0;
    position: fixed;
    top: 0;
    z-index: 910;
    -webkit-transition: margin-top .2s linear;
    transition: margin-top .2s linear;
    min-height: 30px;
    padding: 5px 0;
    width: 100%;
    .col-sm-6 {
    	float: left;
	    width: 50%;
	    .top-navbar {
		    background: none;
		    border: 0 none;
		    border-radius: 0;
		    height: auto;
		    margin: 0;
		    min-height: 30px;
		    .navbar-nav{
		    	#customers{
					margin-top: -5px;
					margin-bottom: -5px;
		    		a{
						font-size: 12px;
						line-height: 22px;
						padding: 9px 15px;
						color: #fff;
		    		}
		    		#customers-content{
		    			border-radius: 3px 0 3px 3px;
						-webkit-box-shadow: 0 2px 2px -1px rgba(0,0,0,.9);
						box-shadow: 0 2px 2px -1px rgba(0,0,0,.9);
						-moz-border-radius: 3px 0 3px 3px;
						-webkit-border-radius: 3px 0 3px 3px;
		    			padding: 15px;
		    			width: 175px;
		    			display: none;
						z-index: 999;
						background: #0098ca;
						position: absolute;
						top: 40px;
						.nav {
						    margin-top: -5px;
						    margin-bottom: -5px;
							float: left;
							margin: 0;
							li {
								float: left;
							    position: relative;
							    display: block;
							    a {
								    font-size: 12px;
								    line-height: 22px;
								    padding: 9px 15px;
								    color: #fff;
									position: relative;
									display: block;
									font-weight: bold;
									&:hover {
									    background: #efefef;
									    color: #7b7b7b;
									    -webkit-transition: background .2s linear,color .2s linear;
									    transition: background .2s linear,color .2s linear;
									}
								}    
							}

						}
		    		}
		    	}
		    	#customers1{
		    		margin-top: -5px;
					margin-bottom: -5px;
		    		a{
						font-size: 12px;
						line-height: 22px;
						padding: 9px 15px;
						color: #fff;
		    		}
		    	}
		    }
		    #login{
		    	margin-top: -5px;
					margin-bottom: -5px;
		    	a{
					font-size: 12px;
					line-height: 22px;
					padding: 9px 15px;
					color: #fff;
	    		}
		    	#login-content{
		    		display: none;
		    	}
		    }
		}
	}
}
